import Form from "./Form"
import {useState} from 'react'
function NavBar(){
    const [isvisible, setisvisible] = useState(false)
    function click(e){
         e.preventDefault()

    setisvisible(isvisible => !isvisible)
    }
return(
    <header>
        <div class="nav">
          <ul class="nav-bar">
            <li class="hoverable"><a href=" ">Home</a></li>
            <li class="hoverable"><a href=" ">About</a></li>
            <li class="hoverable"><a href=" ">FAQ</a></li>
             <li class="hoverable"><a onClick={click} href=" "> Add New Recipe </a>{isvisible ? <Form />: null}</li>
          </ul>
        </div>
        </header>
)
}

export default NavBar
