import {useState} from 'react'
function Form(){
const [isform, setisform] = useState('')
function handleChange(e){
    e.preventDefault()
    setisform({...isform, [e.target.placeholder]: e.target.value})

}
return(
    <form className="new-poem-form">
      <input 
      placeholder="Meal"
      name
      onChange={handleChange} 
      value={isform} 
      />
      <input 
      placeholder="Category"
      onChange={handleChange}  
      value={isform}  
      />
      <input 
      placeholder="Area"
      onChange={handleChange}  
      value={isform} 
      />
      <textarea 
      placeholder="Instructions..." 
      rows={10} 
      onChange={handleChange} 
      value={isform}  
      />
      <input 
      placeholder="Image"
      onChange={handleChange}  
      value={isform}  
      />
      <input 
      placeholder="Video" 
      onChange={handleChange} 
      value={isform}  
      />
      <textarea 
      placeholder="Ingredients..." 
      rows={10}   
      onChange={handleChange}
      value={isform} 
      />
      <input 
      placeholder="Articles"
      onChange={handleChange}  
      value={isform}  
      />
      <input 
      type="submit" 
      value="Share your Recipe"
      onChange={handleChange}  
      />
    </form>
)
}

export default Form
