import {useState} from 'react'
import ReactPlayer from "react-player";
import Favorite from './Favorite';
import styled from 'styled-components';

function RecipeCard({recipe, addToFavorite}){
  const [isvis, setisvis] = useState(false)
  const [liked, setliked] =  useState(true)
  // eslint-disable-next-line no-unused-vars
  function newClick(e){
    console.log(e);
    setisvis(isvis => !isvis)

  }
  function likeBtn(){
    addToFavorite(recipe)  
    setliked(liked => !liked)
    // const newFavorites = addToFavorite.filter(like => console.log(like))
  }
return(

   <RecipeCardContainer >

    <h2 >{recipe.Meal}</h2>
    <h2>({recipe.Area})</h2>
    <ImageContainer>
   <img  onClick={newClick} width="350" height="300" src={recipe.MealThumb} alt="" />
   </ImageContainer>

  { isvis ? <div >

              <h3>Category: {recipe.Category}</h3>
              <p> {recipe.Instructions}</p>
              <div></div>
              <ReactPlayer  url={recipe.Video}/>
              <div></div>
              <p>{recipe.ingredient}</p>
              {liked ? <button onClick={likeBtn}className="emoji-button like">üëçüèæ</button> : null}
              <a href={recipe.Source} target="_blank" rel="noreferrer">{recipe.Source}</a>
            </div>
           : null}

            <Favorite liked={liked} />


   </RecipeCardContainer>


)
}
export default RecipeCard;


const RecipeCardContainer = styled.div`
  display: flex;
  flex-direction: row;


  float: left;
  align-items: flex-start;
  flex-wrap: nowrap;
  font-family: 'Robato', sans-serif;  
  justify-content: space between;
  & h2{
    display: flex;
    flex-direction: row;
    float: left;
    padding: 5px;
  }

`
const ImageContainer =styled.div`
  display: flex;
  width: 100%;
  margin: 10px;


    align-items: flex-start;
  flex-wrap: nowrap;
  font-family: 'Robato', sans-serif;  
  justify-content: space evenly;
  padding : 5px;
  opacity: 0.9;
  width: 100%;
    height: auto;
    transform: scale(1);
    transition: transform 0.8s, opacity 0.5s;
    img:hover{
      opacity: 1;
    transform: scale(1.03);
    }


`
